import styled from 'styled-components';


export const Button = styled.button`
  border-color: hotpink;
  border-width: 1px;
  color: hotpink;
  border-radius: 12px;
  font-size: 1.3rem;
  font-weight: 500;
  background-color: black;
  padding: 0rem 2.5rem;
  height: 40px;
  :hover {
    color: white;
    background-color: rgb(189, 35, 125);
    border: 1px solid rgb(189, 35, 125);
  }
`;
